  selector: 'app-empformview',
  templateUrl: './empformview.component.html',
  styleUrls: ['./empformview.component.scss']
})
export class EmpformviewComponent implements OnInit {

  alldatares!: object;
  constructor(private emp: EmpserviceService) {

  }






  temp = [] = [];
  tempaddr =[]=[];
  alldata() {
    // let name = [];
