import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { EmpformserviceService } from './empformservice.service';

@Component({
  selector: 'app-employeeform',
  templateUrl: './employeeform.component.html',
  styleUrls: ['./employeeform.component.scss']
})
export class EmployeeformComponent implements OnInit {


public signupEmp!:FormGroup

  constructor(private formBuilder:FormBuilder,private empservice: EmpformserviceService) { }


  ngOnInit(): void {

  }

  finaldata:any
  registerEmp(){

   var addr=[];
    var n  = document.getElementById("name");
    var a  = document.getElementById("addr");
    var c  = document.getElementById("city");
    var final:any

    addr.push(a);
    addr.push(c);

    final+=addr
    final+=n

console.log(final)

    this.empservice.submitapi(final).subscribe(responseList=>{
      this.finaldata=responseList;
      this.signupEmp.reset()
      alert("emp registered")
    })

  }






















}

<div class="container">
    <div class="row">
        <div class="col-md-6">
            <div class="card">
                <div class="text-center">
                    <!-- <h1>Signup</h1> -->
                    <h6>Register your self</h6>
                </div>
                <form>
                    <form>
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Name</label>
                            <input type="text"  class="form-control" id="name">
                          </div> 
                           <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">addr</label>
                            <input type="text"   class="form-control" id="addr" placeholder="addr">
                            <input type="text"  class="form-control" id="city" placeholder="city">
                          </div>
                        <button type="submit" (click)="registerEmp()" class="btn btn-primary mb-2">Register</button>
                      </form>
                      <!-- <a style="color: white ; margin-top:5px;">Already register? click to login!!</a> -->
                </form>
            </div>
        </div>
    </div>
</div>
