import { Component, OnInit } from '@angular/core';
import { EmpserviceService } from '../empformpage/empservice.service';

@Component({
  selector: 'app-empformview',
  templateUrl: './empformview.component.html',
  styleUrls: ['./empformview.component.scss']
})
export class EmpformviewComponent implements OnInit {

  alldatares:any
  constructor(private emp:EmpserviceService){

  }







  alldata(){
    this.emp.getalldata().subscribe(responseList=>{

      this.alldatares = responseList;
      for(let i=0;i<this.alldatares.lenght;i++){
        var data = this.alldatares[i].data
        console.log(data)
      }
    })
  }




  ngOnInit():void{
    this.alldata();
  }

}
