import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { EmpformserviceService } from './empformservice.service';

@Component({
  selector: 'app-employeeform',
  templateUrl: './employeeform.component.html',
  styleUrls: ['./employeeform.component.scss']
})
export class EmployeeformComponent implements OnInit {


public signupEmp!:FormGroup

  constructor(private formBuilder:FormBuilder,private empservice: EmpformserviceService) { }


  ngOnInit(): void {
    this.signupEmp = this.formBuilder.group({
      fullname:[''],
      mobile:['']
    })
  }

  finaldata:any
  registerEmp(){
    this.empservice.submitapi(this.signupEmp.value).subscribe(responseList=>{
      this.finaldata=responseList;
    })

  }






















}

<div class="container">
    <div class="row">
        <div class="col-md-6">
            <div class="card">
                <div class="text-center">
                    <!-- <h1>Signup</h1> -->
                    <h6>Register your self</h6>
                </div>
                <form>
                    <form [formGroup]="signupEmp" (ngSubmit)="registerEmp()">
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Full Name</label>
                            <input type="text" formControlName="fullname" class="form-control" id="exampleInputPassword1">
                          </div> 
                           <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Mobile No</label>
                            <input type="number" formControlName="mobile" class="form-control" id="exampleInputPassword1">
                          </div>
                        <!-- <button type="submit" class="btn btn-primary mb-2">Signup</button> -->
                      </form>
                      <!-- <a style="color: white ; margin-top:5px;">Already register? click to login!!</a> -->
                </form>
            </div>
        </div>
    </div>
</div>
