import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { EmpserviceService } from '../empformpage/empservice.service';
import { EmpModel } from './emp.model';

@Component({
  selector: 'app-empformview',
  templateUrl: './empformview.component.html',
  styleUrls: ['./empformview.component.scss']
})
export class EmpformviewComponent implements OnInit {

  alldatares!: object;

  employeeModelObj: EmpModel = new EmpModel();

  public formvalue!: FormGroup
  constructor(private fb: FormBuilder, private emp: EmpserviceService) {

  }

  ngOnInit(): void {
    this.formvalue = this.fb.group({
      name: ['', [Validators.required]],
      addr: ['', [Validators.required]],
      city: ['', [Validators.required]]
    })
    this.alldata();
  }

  Editresponse: any
  editclick() {
    this.employeeModelObj.name = this.formvalue.value.name;
    this.employeeModelObj.addr = this.formvalue.value.addr;
    this.employeeModelObj.city = this.formvalue.value.city;
    // console.log(this.employeeModelObj, "modelclickdata")
    this.emp.updateEmp(this.employeeModelObj, this.employeeModelObj.id).subscribe((response) => {
      this.Editresponse = response;
      this.formvalue.reset()
      this.ngOnInit();
    })
  }



  onEdit(row: any) {
    this.employeeModelObj.id = row.id;
    this.formvalue.controls['name'].setValue(row.name);
    this.formvalue.controls['addr'].setValue(row.address.addr);
    this.formvalue.controls['city'].setValue(row.address.city);

  }

  finalresponse: any
  onDelete(row: any) {
    this.emp.delEmp(row.id).subscribe(responseList => {
      alert("Emp Deleted")
      this.ngOnInit()
    })
  }



  temp: any;
  tempaddr = [] = [];
  alldata() {
    this.emp.getalldata().subscribe((response) => {
      for (let i = 0; i < response.response.length; i++) {
        //console.log(response.response)
        this.temp = response.response
      }
    })
  }


  arraydata:any;

  arraydataFun(){
    this.emp.getarraydata().subscribe(responseList=>{
      this.arraydata = responseList;
    })
  }





}
<nav class="navbar navbar-light" style="background-color:rgb(201, 235, 174);border-bottom: 1px solid black;">
    <div class="container-fluid">
        <h1 style="color: rgb(9, 9, 9);">Angular CRUD with NODE.JS</h1>
        <div class="d-flex">

            <a routerLink="/dashboard">Dashboard CRUD json</a>
        </div>
    </div>
 </nav>






<body style="background-color: powderblue; width:100%;height:100%">

    <div class="container" style="padding:10px ; background-color: antiquewhite;">
        <div class="card">
            <div class="card-body">
                <table class="table mt-3">
                    <thead>
                        <tr>
                            <th scope="col">Emp Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Address</th>
                            <th scope="col">City</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let row of temp">
                            <td>{{row.id}}</td>
                            <td>{{row.name}}</td>
                            <td>{{row.address.addr}}</td>
                            <td>{{row.address.city}}</td>
                            <td>
                                <button data-bs-toggle="modal" data-bs-target="#exampleModal" class="btn btn-info"
                                    (click)="onEdit(row)">Edit</button>
                                <button (click)="onDelete(row)" class="btn btn-danger mx-3">Delete</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <div class="row">
                    <div>
                       <a class="btn btn-success" routerLink="/emp">Register</a>
                    </div>
                </div>

            </div>
        </div>






        <!-- Modal -->
        <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div class="modal-dialog">
                <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="exampleModalLabel">Edit Employee</h5>
                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div class="modal-body">
                        <form [formGroup]="formvalue">
                            <div class="mb-3">
                                <label for="exampleInputPassword1" class="form-label"> Name</label>
                                <input type="text" class="form-control" formControlName="name" id="exampleInputEmail1"
                                    aria-describedby="emailHelp">
                            </div>
                            <div class="mb-3">
                                <label for="exampleInputPassword1" class="form-label">Addr </label>
                                <input type="text" class="form-control" formControlName="addr" id="exampleInputPassword1">
                            </div>
                            <div class="mb-3">
                                <label for="exampleInputPassword1" class="form-label">City</label>
                                <input type="text" class="form-control" formControlName="city" id="exampleInputPassword1">
                            </div>
                        </form>
                    </div>
                    <div class="modal-footer">
                        <button type="button" id="cancel" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                        <button type="button" class="btn btn-primary" data-bs-dismiss="modal" (click)="editclick()">Update</button>
                    </div>
                </div>
            </div>
        </div>









        <div class="card">
            <div class="card-body">
                <table class="table mt-3">
                    <thead>
                        <tr>
                            <th scope="col">Emp Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Address</th>
                            <th scope="col">City</th>
                            <th scope="col">A/C Number</th>
                            <th scope="col">Bank Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr *ngFor="let row of arraydata">
                            <td>{{row.id}}</td>
                            <td>{{row.name}}</td>
                            <td>{{row.address.addr}}</td>
                            <td>{{row.address.city}}</td>
                            <td>{{row.bank.acnumber}}</td>
                            <td>{{row.bank.acname}}</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>













    </div>

</body>
